sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  try{

  var vcnUtil = new OracleVirtualCloudNetworkUtils();
  vcnUtil.validateInputVariables(inputs, outputs);
  vcnUtil.validateMandatoryVariables(inputs,["networkId","compartmentId"]);
  outputs = vcnUtil.createSecListPayLoad(inputs,outputs);

  }
  catch(ex){

    throw new Error(ex.message);

  }
})(inputs, outputs);

