sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  try{
  var vcnUtil = new OracleVirtualCloudNetworkUtils();
  vcnUtil.validateInputVariables(inputs, outputs);
  vcnUtil.validateMandatoryVariables(inputs,["vcnId","compartmentId","ingressSecurityRules","egressSecurityRules"]);
  var payload = vcnUtil.createPayload(inputs,["vcnId","compartmentId","displayName"]);
  var payloadTest =  vcnUtil.createPayload(inputs.ingressSecurityRules,["protocol","tcpOptions","source"]);
  var payloadTest1 =  vcnUtil.createPayload(inputs.egressSecurityRules,["protocol","tcpOptions","source"]);
  payload += payloadTest;
    payload += payloadTest1;
  outputs.payload = JSON.stringify(payload);

  }
  catch(ex){

    throw new Error("Mandatory Fields Should be provided");

  }
})(inputs, outputs);

