sys_variable_value.value:
(function execute(inputs, outputs) {
  // ... code ...
  var data = new Array();
  var choiceData = new Array();
  var document;
  var documents = new Object();
  var choiceDocument = new Object();

  if (inputs.status == '200') {
    try {
      var responseData = JSON.parse(inputs.response);
      outputs.documents = JSON.stringify(responseData);
      for (var i = 0; i < responseData.length; i++) {
        var choice_doc = new Object();
        choice_doc.label = responseData[i].displayName;
        choice_doc.name = responseData[i].id;
        choiceData.push(choice_doc);
      }
    } catch (ex) {
      var dummyDoc = new Object();
      dummyDoc.label = '<No Network retrieved>';
      dummyDoc.name = null;
      data.push(dummyDoc);
    } finally {
      choiceDocument.data = choiceData;
      outputs.choicedocument = choiceDocument;
      outputs.status = "Success";
    }
  } else {
    var errMsgs = new OracleVCN_ErrorHandling().errorHandler(inputs.response);
    outputs.error_message = errMsgs[0];
    outputs.status = errMsgs[1];
  }
})(inputs, outputs);

