sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  try{
  var vcnUtil = new OracleVirtualCloudNetworkUtils();
  vcnUtil.validateInputVariables(inputs, outputs);
  vcnUtil.validateMandatoryVariables(inputs,["compartmentId"]);
  outputs.compartment_id = inputs.compartmentId;

  }
  catch(ex){

    throw new Error("Mandatory Fields Should be provided");

  }
})(inputs, outputs);

