sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  var data = new Array();
    var document;
    var documents = new Object();

    if (inputs.status == '200') {
        try {
            var responseData = JSON.parse(inputs.response);
            for (var i = 0; i < responseData.length; i++) {
                var document = new Object();
                document.cidrblock = responseData[i].cidrBlock;
                document.compartmentid = responseData[i].compartmentId;
                document.dhcpoptionsid = responseData[i].dhcpOptionsId;
                document.displayname = responseData[i].displayName;
                document.id = responseData[i].id;
                document.lifecyclestate = responseData[i].lifecycleState;
                document.routetableid = responseData[i].routeTableId;
                document.securitylistids = responseData[i].securityListIds;
                document.timecreated = responseData[i].timeCreated;
                document.vcnid = responseData[i].vcnId;
                document.virtualrouterip = responseData[i].virtualRouterIp;
                document.virtualroutermac = responseData[i].virtualRouterMac;
                document.prohibitpubliciponvnic = responseData[i].prohibitPublicIpOnVnic;
                document.definedtags = responseData[i].definedTags;
                document.freeformtags = responseData[i].freeformTags;
              	data.push(document);

            }
        } catch (ex) {
            var dummyDoc = new Object();
            dummyDoc.label = '<No Subnet retrieved>';
            dummyDoc.name = null;
            data.push(dummyDoc);
        } finally {
            documents.data = data;
            outputs.documents = documents;
            outputs.status = "Success";
        }
    } 
  else {
        var errMsgs = new OracleVCN_ErrorHandling().errorHandler(inputs.response);
        outputs.error_message = errMsgs[0];
        outputs.status = errMsgs[1];
    }
})(inputs, outputs);

