sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  try{
    var vcnUtil = new OracleVirtualCloudNetworkUtils();
    vcnUtil.validateInputVariables(inputs, outputs);
    vcnUtil.validateMandatoryVariables(inputs,["compartment_id"]);
    outputs.compartment_id = inputs.compartment_id;
    outputs.network_id = inputs.network_id;
  }
  catch(ex){

    throw new Error("Mandatory Fields Should be provided");

  }
})(inputs, outputs);

