sys_variable_value.value:
(function execute(inputs, outputs) {
    // ... code ...
    try {
        var vcnUtil = new OracleVirtualCloudNetworkUtils();
        vcnUtil.validateInputVariables(inputs, outputs);
        vcnUtil.validateMandatoryVariables(inputs, ["securityListId"]);
        vcnUtil.updateSecListPayLoad(inputs,outputs);
    } catch (ex) {
        throw new Error("Mandatory fields should be provided");
    }
})(inputs, outputs);

