sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  if(inputs.status == "200" || inputs.status == "201"){
    try{
      		var responseData = JSON.parse(inputs.response);
  			outputs.displayname = responseData.displayName;
            outputs.lifecyclestate = responseData.lifecycleState;
            outputs.timecreated = responseData.timeCreated;
            outputs.vcnid = responseData.vcnId;
      		outputs.status = "success";
      		outputs.definedtags = responseData.definedTags;
    }
  catch(ex){
	throw new Error('JSON Parse Error. Response Body may be empty.');
  }
}
 else {
       var errorData = new OracleVCN_ErrorHandling().errorHandler(inputs.response);
   		outputs.errormessage = errorData[0];
        outputs.status = errorData[1];
    }
})(inputs, outputs);

