sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  try{
  var vcnUtil = new OracleVirtualCloudNetworkUtils();
  vcnUtil.validateInputVariables(inputs, outputs);
  vcnUtil.validateMandatoryVariables(inputs,["compartment_id","network_id"]);
  var payload = {"compartmentId":inputs.compartment_id};
   outputs.payload = JSON.stringify(payload	);
  outputs.network_id = inputs.network_id;

  }
  catch(ex){

    throw new Error("Mandatory Fields Should be provided");

  }
})(inputs, outputs);

