sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  try{
  var vcnUtil = new OracleVirtualCloudNetworkUtils();
  vcnUtil.validateInputVariables(inputs, outputs);
  vcnUtil.validateMandatoryVariables(inputs,["rtId"]);
   outputs.rtid = inputs.rtId;
  var payload = vcnUtil.createPayload(inputs,["displayName","routeRules"]);                                  
  vcnUtil.trimValues(inputs.freeformTags);
  var tags = vcnUtil.createTagsPayload(inputs.freeformTags);  
  if(Object.keys(tags).length){
        payload.freeformTags = tags;
  }
   outputs.payload = JSON.stringify(payload);


  }
  catch(ex){

    throw new Error("Mandatory Fields Should be provided");

  }
})(inputs, outputs);

