sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
   if(inputs.status == "200" || inputs.status == "201"){
    try{
      var responseData = JSON.parse(inputs.response);
    outputs.status = "Success";
    outputs.cidrblock = responseData.cidrBlock;
    outputs.compartmentid = responseData.compartmentId;
    outputs.displayname = responseData.displayName;
    outputs.id = responseData.id;
    outputs.defaultdhcpoptionsid = responseData.defaultDhcpOptionsId;
    outputs.defaultroutetableid = responseData.defaultRouteTableId;
    outputs.defaultsecuritylistid = responseData.defaultSecurityListId;
    outputs.lifecyclestate = responseData.lifecycleState;
    outputs.freeformTags = responseData.freeformTags;
    outputs.responsebody = responseData;

    }
  catch(ex){
	throw new Error('JSON Parse Error. Response Body may be empty.');
  }
}
 else {

    }
})(inputs, outputs);

