sys_script_include.script:
var OracleVCN_ErrorHandling = Class.create();
OracleVCN_ErrorHandling.prototype = {
    initialize: function() {},

	networkManagementErrorHandling : function(inputs, outputs){
		var errorMessage = JSON.parse(inputs["response"]).message;
		var statusCode = inputs["status"];
        outputs.status = "Error";
        if (statusCode == "404") {
            if (errorMessage.indexOf("does not exist") != -1) {
                outputs.errormessage = "Network Id not exists.";
            }
        }
		if (statusCode == "409") {

        }
        if (!outputs.errormessage)
            outputs.errormessage = errorMessage;
        return outputs;
	},
    type: 'OracleVCN_ErrorHandling'
};
